import React, { useReducer } from 'react';
import { v4 as uuid } from 'uuid'

import ContactContext from './contactContext';
import ContactReducer from './contactReducer';
import {
    ADD_CONTACT,
    DELETE_CONTACT,
    SET_CURRENT,
    UPDATE_CONTACT,
    FILTER_CONTACT,
    CLEAR_FILTER,
    CLEAR_CURRENT
} from '../types';

const ContactState = props => {
    const initialState = {
        contacts: [
            {
                id: 1,
                name: "Abhishek",
                email: "abhi@gmail.com",
                phone: "8976767676",
                type: "personal"
            },
            {
                id: 2,
                name: "Shalu",
                email: "shalu@gmail.com",
                phone: "5645654654645",
                type: "professional"
            },
            {
                id: 3,
                name: "Aarushi",
                email: "aaru@gmail.com",
                phone: "324324234334",
                type: "personal"
            }
        ],
        current: null,
        filtered: null
    };

    const [state, dispatch] = useReducer(ContactReducer, initialState);

    //Add contact
    const addContact = contact => {
        contact.id = uuid();
        dispatch({
            type: ADD_CONTACT,
            payload: contact
        })
    }
    //Delete Contact
    const deleteContact = id => {
        dispatch({
            type: DELETE_CONTACT,
            payload: id
        })
    }
    //Set Current Contact
    const setCurrent = contact => {
        dispatch({
            type: SET_CURRENT,
            payload: contact
        })
    }
    //Clear current Contact
    const clearCurrent = () => {
        dispatch({
            type: CLEAR_CURRENT
        })
    }
    //Update contact
    const updateContact = contact => {
        dispatch({
            type: UPDATE_CONTACT,
            payload: contact
        })
    }
    //Filter contacts
    const filterContacts = text => {
        dispatch({
            type: FILTER_CONTACT,
            payload: text
        })
    }
    //Clear filter
    const clearFilter = () => {
        dispatch({
            type: CLEAR_FILTER
        })
    }
    return (
        <ContactContext.Provider
            value={{
                contacts: state.contacts,
                current: state.current,
                filtered: state.filtered,
                addContact,
                deleteContact,
                setCurrent,
                clearCurrent,
                updateContact,
                filterContacts,
                clearFilter
            }}>
            {props.children}
        </ContactContext.Provider>
    )
}

export default ContactState;
